import requests
import os, re

from dotenv import load_dotenv
from crewai.tools import tool


load_dotenv()

FIRECRAWL_ENDPOINT = "https://api.firecrawl.dev/v2/search"
FIRECRAWL_API_KEY = os.getenv("FIRECRAWL_API_KEY")


@tool
def web_job_search_tool(query: str) -> str:
    headers = {
        "Authorization": f"Bearer {FIRECRAWL_API_KEY}",
        "Content-Type": "application/json",
    }

    payload = {
        "query": query,
        "sources": ["web"],
        "categories": [],
        "limit": 5,
        "scrapeOptions": {"maxAge": 172800000, "formats": ["markdown"]},
    }

    try:
      response = requests.post(FIRECRAWL_ENDPOINT, json=payload, headers=headers)
      response.raise_for_status()
      response_data = response.json()

      if not response_data.get("success"):
        return "Error: Failed to search the web with search tool"

      results = []
      # ë¶ˆí•„ìš”í•œ ë¬¸ìì—´ parsing -> í† í° ì¤„ì´ê¸°
      for data in response_data.get("data").get("web"):
        # \\+ ğŸ‘‰ğŸ» \ê°€ 1ë²ˆ ì´ìƒë˜ëŠ” ê²ƒ
        # | ğŸ‘‰ğŸ» or
        # \n ğŸ‘‰ğŸ» ê°œí–‰
        # ì¦‰, \ê°€ 1ë²ˆ ì´ìƒì´ê±°ë‚˜ \nì´ë©´ ì œê±°.
        cleaned_markdown = re.sub(r"\\+|\n", "", data.get("markdown") or "").strip()
        # markdown link ì œê±° ì •ê·œì‹
        cleaned_markdown = re.sub(r"\[[^\]]+\]\([^\)]+\)|https?://[^\s]+", "", cleaned_markdown)
        result = { 
            "title": data.get("title") or "",
            "url": data.get("url") or "",
            
            "markdown": cleaned_markdown
        }

        results.append(result)

      return results

    except Exception as e:
      return f"Error:> {e}"



